# Supabase configuration (set SUPABASE_URL and SUPABASE_KEY as environment variables)
supabase_url: str = os.environ.get("SUPABASE_URL")
supabase_key: str = os.environ.get("SUPABASE_KEY")
supabase: Client = create_client(supabase_url, supabase_key)


def validate_form(form):
    errors = {}
    full_name = form.get('full_name', '').strip()
    if not full_name:
        errors['full_name'] = 'Full name is required.'

    email = form.get('email', '').strip()
    if not email:
        errors['email'] = 'Email is required.'
    elif not re.match(r'^[^@]+@[^@]+\.[^@]+$', email):
        errors['email'] = 'Invalid email format.'

    phone = form.get('phone', '').strip()
    if not phone:
        errors['phone'] = 'Phone is required.'

    role = form.get('role', '').strip()
    if not role:
        errors['role'] = 'Role is required.'

    return errors, full_name, email, phone, role


@app.route('/add_employee', methods=['GET', 'POST'])
def add_employee():
    if request.method == 'POST':
        errors, full_name, email, phone, role = validate_form(request.form)
        if errors:
            return render_template('add_employee.html',
                                   errors=errors,
                                   full_name=full_name,
                                   email=email,
                                   phone=phone,
                                   role=role)
        else:
            try:
                supabase.table('employees').insert({
                    'full_name': full_name,
                    'email': email,
                    'phone': phone,
                    'role': role
                }).execute()
                return render_template('success.html')
            except Exception as e:
                errors['general'] = f'Error adding employee: {str(e)}'
                return render_template('add_employee.html',
                                       errors=errors,
                                       full_name=full_name,
                                       email=email,
                                       phone=phone,
                                       role=role)
    return render_template('add_employee.html')


